const context = React.createContext()

const CameraControls = () => {
  //getting the camera and the renderer "gl" from the useThree() functions which returneth them and others
  const {camera, gl} = useThree();
  return (
    //this HAS to be orbitControls not OrbitControls for some reason
    //if starts with capital gives "need new invoke three.js blah blah" error
    <orbitControls args={[camera, gl.domElement]}/>
  )
}

function Box(props) {
  // This reference gives us direct access to the THREE.Mesh object
  //usereft is like declaring and setting a variable in a class from its parameterx 
  const ref = useRef();
  // Hold state for hovered and clicked events
  // line 65 in laymans pseudocode:  State Hovered = false; func setHover(updatedState){hovered = updatedState}
  const [hovered, hover] = useState(false);
  const [clicked, click] = useState(false);
  // const boxPosition = [00, 0, 0]
  // Subscribe this component to the render-loop, rotate the mesh every frame
  // useFrame((state, delta) => (ref.current.rotation.x += 0.01))
  // Return the view, these are regular Threejs elements expressed in JSX
  return (
    <mesh
      {...props}
      // {...boxPosition}
      ref={ref}
      scale={3}
      // onClick={(event) => click(!clicked)}
      // onPointerOver={(event) => hover(true)}
      // onPointerOut={(event) => hover(false)}
      >

      <boxGeometry args={[1, 1, 1]} />
      <meshStandardMaterial color={hovered ? 'hotpink' : 'orange'} />

    </mesh>
  )
}

const Outliner = props => {

  // props will be mesh = {Box} i
  // should return <outline 
  const GeometryRef = useRef();
  return (
     // make a regular render of passed mesh <box>
    <>
      <props.children.type/>
      <props.children.type>
        <meshStandardMaterial depthWrite = {false} color = "#f3bf10" side = {BackSide}/> 
      </props.children.type>
    </>
     // make a depthwrite= false attributed component of passed mesh <box depthwrite =false blah = THREE.backside>
  )

}

export default function App() {
  return (
    //empty tags is react.fragment, DOM-like structure without actually modifying elements
    <div style= {{position: 'absolute', width: '100%', height: '100%'}}>
      <Canvas>
        <ambientLight intensity={0.5} />
        <spotLight position={[10, 10, 10]} angle={0.15} penumbra={1} />
        <pointLight position={[-10, -10, -10]} />
        <Outliner>
          <Box position={[0, 0, 0]} />
        </Outliner>
        {/* <Box position = {[0,0,0]} /> */}
        <CameraControls/>
      </Canvas>
  {/* <counter/> */}
    </div>
  )
}
